package com.zhenwei.demo.gaiatest.demo;

import cn.org.bjca.gaia.util.encoders.Base64;
import cn.org.bjca.gaia.util.encoders.Hex;
import org.junit.Test;

public class Base64Test {


  private static String pub = "";

  public static void main(String[] args) {

    baseTest();
  }


  public static void baseTest(){
    byte[] decode = Base64.decode(pub);
    System.out.println(Hex.toHexString(decode));
  }


  public static void testHexBase(){
    String s = "";
    System.out.println(s.length()/4*3);
    byte[] decode = Base64.decode(s);
    String s1 = Hex.toHexString(decode);
    System.out.println(s1);
    byte[] decode1 = java.util.Base64.getDecoder().decode(s);
    String s2 = Hex.toHexString(decode1);
    System.out.println(s2);

  }



  @Test
  public void decodeP7b(){
    String p7b ="";
    byte[] decode = Base64.decode(p7b);
    System.out.println(new String(decode));
  }


}
