    if (bjcaKey.getKeyType().toUpperCase().contains("SM2")){
      algPolicy = new AlgPolicy(AlgPolicy.SM3_SM2);
    }else {
      algPolicy = new AlgPolicy(AlgPolicy.SHA256_RSA);
    }
    byte[] pkcs7Sign = pkcs7Util
        .pkcs7Sign(algPolicy, bjcaKey, certificate, "123".getBytes(),
            true);
    System.out.println(Base64.toBase64String(pkcs7Sign));
    byte[] pkcs7Sign1 = pkcs7Util.verifyPkcs7Sign(pkcs7Sign, "123".getBytes());
